mapper.select:
SELECT memberId, memberPass
FROM hello_member
WHERE memberId = #{memberId}

SELECT count(*)
FROM hello_member
WHERE memberId = #{memberId}

mapper.update:
UPDATE hello_member
SET memberPass = #{memberPass}
WHERE memberId = #{memberId}

mapper.select:
SELECT num, title, content, writer
FROM hello_board
WHERE num = #{num}

mapper.update:
UPDATE hello_board b, hello_member m
SET b.title = #{title},
    b.content = #{content},
    b.regdate = now()
WHERE num = #{num}
  and b.writer in (
    select memberId from hello_member
    where memberId = #{writer}
    )
